import logo from './logo.svg';
import './App.css';
import {Component} from "react";
import { saveAs } from 'file-saver';
import watermark from "watermarkjs/lib";


class App extends Component {
    state =  {
        selectedFile: null,
        imagePreviewUrl: null,
        selectedChain: null,
    };
    fileChangedHandler = event => {
        this.setState({
            selectedFile: event.target.files[0]
        })

        let reader = new FileReader();

        reader.onloadend = () => {
            this.setState({
                imagePreviewUrl: reader.result
            });
            let image = document.createElement("img");

            watermark([reader.result])
                .image(watermark.text.lowerRight('MyPhoto', '28px serif', '#fff', 0.5))
                .then(function (img) {
                    document.getElementById("text").appendChild(img);
                });
        }
        reader.readAsDataURL(event.target.files[0])

    }

    chainSelectedHandler = event => {
        const upload = document.querySelector('img').src;

    }

    submit = () => {
        let img = document.querySelector('img');
        let imagePath = img.getAttribute('src');
        saveAs(imagePath, 'my-nft.jpg');
    }
    render() {


        let $imagePreview = (<div className="previewText image-container">Please select an Image for Preview</div>);
        if (this.state.imagePreviewUrl) {
            $imagePreview = (<div className="image-container" ><img src={this.state.imagePreviewUrl} alt="icon" width="200" download="my-nft.jpg" /> </div>);
        }
    return (
        <div className="App">
            <h1>Postchain Â© </h1>
            <h3> Keep your NFT first articles safe from theft. </h3>
            <div className="App">
                <label>Select a blockchain where you will be publishing your NFT. </label>
                <select value={this.selectedChain} onChange={this.chainSelectedHandler}>
                    <option value="BSV">Bitcoin</option>
                    <option value="Ethereum">Ethereum</option>
                </select>
                <hr />
                <input type="file" name="avatar" className={"btn btn-grey"} onChange={this.fileChangedHandler}/>
                <br />
                <button type="button" onClick={this.submit} className={"btn btn-green"}> Download </button>
                {$imagePreview}

                <div id="text">
                    <pre>TEXT</pre>
                </div>
            </div>
        </div>
    )
    }
}


export default App;
